mapper.select:
select d.dept_id AS deptId, d.parent_id AS parentId, d.ancestors AS ancestors, d.dept_name AS deptName, d.order_num AS orderNum, d.leader AS leader, d.phone AS phone, d.email AS email, d.status AS status, d.del_flag AS delFlag, d.create_by AS createBy, d.create_time AS createTime, combo.combo_name AS comboName, combo.combo_efficient_days - DATEDIFF(NOW(),deptcom.create_time) AS comboEfficientDays from sys_dept d left join mercaodo_deptcombo deptcom ON deptcom.dept_id = d.dept_id left join mercado_combo_info combo ON deptcom.combo_id = combo.id where d.del_flag = '0'
AND dept_id = #{deptId}
AND parent_id = #{parentId}
AND dept_name like concat('%', #{deptName}, '%')
AND status = #{status}
${params.dataScope} order by d.parent_id, d.order_num

select d.dept_id from sys_dept d left join sys_role_dept rd on d.dept_id = rd.dept_id where rd.role_id = #{roleId}
and d.dept_id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.dept_id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.order_num

mapper.update:
update sys_dept
parent_id = #{parentId}, dept_name = #{deptName}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where dept_id = #{deptId}

update sys_dept set ancestors =

    when #{item.deptId} then #{item.ancestors}

where dept_id in

    #{item.deptId}

mapper.delete:
update sys_dept
set del_flag = '2'
where dept_id = #{deptId}
